import { Component, OnInit } from '@angular/core';
import { Router, NavigationEnd } from '@angular/router';
import { filter } from 'rxjs';
import { AuthService } from './components/auth/auth.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html'
})
export class AppComponent implements OnInit {
  constructor(
    private router: Router,
    private authService:AuthService  
  ) {}

  ngOnInit(): void {
    this.authService.getUserSession().subscribe(data=>{
      console.log(data)
        if(data.isAuthenticated){
          this.authService.setIsAuthenticate(true)
          this.authService.setSessionTimer(data.expiresIn)
          this.authService.expiresIn=data.expiresIn;
          this.authService.userDetails=data.user
        }
    })
  }

}
